-- PostgreSQL схема для Django моделей
-- Создание расширения для UUID
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";

-- Таблица категорий товаров (с поддержкой переводов)
CREATE TABLE category (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    name VARCHAR(255) NOT NULL UNIQUE,
    name_en VARCHAR(255),
    name_ru VARCHAR(255),
    name_ky VARCHAR(255),
    slug VARCHAR(255) NOT NULL UNIQUE,
    description TEXT DEFAULT '',
    description_en TEXT,
    description_ru TEXT,
    description_ky TEXT
);

-- Индекс для created_at (как указано в CoreModel)
CREATE INDEX idx_category_created_at ON category(created_at);

-- Таблица причин бойкота (с поддержкой переводов)
CREATE TABLE reason (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    title VARCHAR(255) NOT NULL,
    title_en VARCHAR(255),
    title_ru VARCHAR(255),
    title_ky VARCHAR(255),
    description TEXT NOT NULL,
    description_en TEXT,
    description_ru TEXT,
    description_ky TEXT
);

-- Индекс для created_at
CREATE INDEX idx_reason_created_at ON reason(created_at);

-- Таблица товаров (с поддержкой переводов)
CREATE TABLE product (
    id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    name VARCHAR(255) NOT NULL,
    name_en VARCHAR(255),
    name_ru VARCHAR(255),
    name_ky VARCHAR(255),
    description TEXT DEFAULT '',
    description_en TEXT,
    description_ru TEXT,
    description_ky TEXT,
    is_boycotted BOOLEAN NOT NULL DEFAULT FALSE,
    query_count INTEGER NOT NULL DEFAULT 0,
    is_kyrgyz_product BOOLEAN NOT NULL DEFAULT FALSE,
    image VARCHAR(255),
    category_id UUID NOT NULL REFERENCES category(id) ON DELETE CASCADE,
    boycott_reason_id UUID REFERENCES reason(id) ON DELETE SET NULL
);

-- Индексы для product
CREATE INDEX idx_product_created_at ON product(created_at);
CREATE INDEX idx_product_updated_at ON product(updated_at);
CREATE INDEX idx_product_category_id ON product(category_id);
CREATE INDEX idx_product_boycott_reason_id ON product(boycott_reason_id);
CREATE INDEX idx_product_query_count ON product(query_count);

-- Дополнительные индексы для переводимых полей
CREATE INDEX idx_category_name_en ON category(name_en);
CREATE INDEX idx_category_name_ru ON category(name_ru);
CREATE INDEX idx_category_name_ky ON category(name_ky);

CREATE INDEX idx_product_name_en ON product(name_en);
CREATE INDEX idx_product_name_ru ON product(name_ru);
CREATE INDEX idx_product_name_ky ON product(name_ky);

CREATE INDEX idx_reason_title_en ON reason(title_en);
CREATE INDEX idx_reason_title_ru ON reason(title_ru);
CREATE INDEX idx_reason_title_ky ON reason(title_ky);

-- Таблица для связи многие-ко-многим (alternative_products)
CREATE TABLE product_alternative_products (
    id SERIAL PRIMARY KEY,
    from_product_id UUID NOT NULL REFERENCES product(id) ON DELETE CASCADE,
    to_product_id UUID NOT NULL REFERENCES product(id) ON DELETE CASCADE,
    UNIQUE(from_product_id, to_product_id)
);

-- Индексы для таблицы связей
CREATE INDEX idx_product_alternative_from ON product_alternative_products(from_product_id);
CREATE INDEX idx_product_alternative_to ON product_alternative_products(to_product_id);

-- Функция для автоматического обновления updated_at
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = NOW();
    RETURN NEW;
END;
$$ language 'plpgsql';


-- Триггеры для автоматического обновления updated_at при изменении записей
CREATE TRIGGER update_category_updated_at BEFORE UPDATE ON category
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_reason_updated_at BEFORE UPDATE ON reason
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_product_updated_at BEFORE UPDATE ON product
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();


-- Комментарии к таблицам
COMMENT ON TABLE category IS 'Категории товаров с поддержкой многоязычности (en, ru, ky)';
COMMENT ON TABLE reason IS 'Причины бойкота с поддержкой многоязычности (en, ru, ky)';
COMMENT ON TABLE product IS 'Основная модель товара с поддержкой многоязычности (en, ru, ky)';
COMMENT ON TABLE product_alternative_products IS 'Связь многие-ко-многим для альтернативных товаров';

-- Комментарии к полям
COMMENT ON COLUMN product.query_count IS 'Количество запросов для расчета рейтинга';
COMMENT ON COLUMN product.is_kyrgyz_product IS 'Является ли товар кыргызским';

-- Комментарии к переводимым полям
COMMENT ON COLUMN category.name_en IS 'Название категории на английском';
COMMENT ON COLUMN category.name_ru IS 'Название категории на русском';
COMMENT ON COLUMN category.name_ky IS 'Название категории на кыргызском';

COMMENT ON COLUMN product.name_en IS 'Название товара на английском';
COMMENT ON COLUMN product.name_ru IS 'Название товара на русском';
COMMENT ON COLUMN product.name_ky IS 'Название товара на кыргызском';

COMMENT ON COLUMN reason.title_en IS 'Заголовок причины бойкота на английском';
COMMENT ON COLUMN reason.title_ru IS 'Заголовок причины бойкота на русском';
COMMENT ON COLUMN reason.title_ky IS 'Заголовок причины бойкота на кыргызском';








